<?php get_header(); ?>		<div id="single-content">			<div id="post-home" class="clearfix">				<?php if(have_posts()) : while (have_posts()) : the_post(); ?>				<div class="post-content">					<?php $postid = get_the_ID();$cnt = post_img_number();$img = wim_single($postid,0);if($img): ?>						<div id="img" data-id="<?php echo $postid;?>">							<div id="img-loading"></div>							<a id="img-left" title="<?php _e('No previous','iphoto');?>"></a>							<a id="img-right" title="<?php if($cnt>1){_e('Next photo','iphoto');}else{_e('No next','iphoto');}?>"></a>							<div id="img-number"><span id="img-current">1</span>&#47;<span id="img-cnt"><?php echo post_img_number();?></span></div>							<p><?php echo $img;?></p>						</div>					<?php else : ?>						<?php the_content(); ?>					<?php endif; ?>				</div>				<?php endwhile; endif; ?>				<div id="postMsg" class="clearfix">					<div class="views"><?php if(function_exists('the_views')) {$views = the_views(0);preg_match('/\d+/', $views, $match);echo '<span>阅读</span><br /><span>'.$match[0].'</span>';} ?></div>					<?php if(function_exists('wizylike')) wizylike('button2');  ?>					<div class="comments"><span>评论</span><br /><span><?php comments_number('0','1','%'); ?></span></div>					<?php if ((current_user_can('level_0'))){ ?>						<div id="imgdl">							<a href="<?php $postid = (int)$postid+10;$dl = base64_encode($postid);$page = get_page_by_title( 'download' );$page_id = $page->ID;if ($page_id!=""){ ?><?php echo get_page_link( $page_id ).'&dl='.$dl;?><?php }?>" target="_blank"><?php _e('Download','iphoto');?></a>						</div>					<?php } else { ?>						<div id="imgdl">							<a href="#" target="_blank"><?php _e('Download','iphoto');?></a>						</div>						<div id="imgReg">							<p><?php _e('You must register before you can download.','iphoto');?><span id="imgClose">x</span></p>							<div id="imgRegCon" class="clearfix">								<a href="<?php echo get_option('home'); ?>/wp-register.php"  id="imgRegister"><?php _e('Register','iphoto');?></a>								<a href="#"  id="imgLogin"><?php _e('Log in','iphoto');?></a>							</div>						</div>							<script type="text/javascript">jQuery(document).ready(function($){var $body=(window.opera)?(document.compatMode=="CSS1Compat"?$("html"):$("body")):$("html,body"),b=function(){$("#imgReg").fadeOut();$("#reg-lay").fadeOut()};$body.append('<div id="reg-lay"></div>');$('#imgdl a').click(function(e){e.preventDefault();$("#reg-lay").show();$("#imgReg").show();return false});$('#imgClose').click(function(e){e.preventDefault();b();return false});$('#imgLogin').click(function(e){e.preventDefault();$('#login a').click();b();return false});$body.click(function(e){b()})});</script>					<?php }?>				</div>				<div id="comments">					<?php comments_template('', true); ?>				</div>			</div>		</div><?php get_sidebar(); ?><?php get_footer(); ?>